const callLogId = $input.query.call_log_id;
const userId = $input.query.user_id;

// Generate TwiML response to stream to ElevenLabs
const twiml = `
<Response>
  <Connect>
    <Stream url="wss://api.elevenlabs.io/v1/convai/conversation">
      <Parameter name="agent_id" value="${process.env.ELEVENLABS_AGENT_ID}" />
      <Parameter name="api_key" value="${process.env.ELEVENLABS_API_KEY}" />
      <Parameter name="call_log_id" value="${callLogId}" />
      <Parameter name="user_id" value="${userId}" />
    </Stream>
  </Connect>
</Response>
`;

return {
  json: {
    twiml: twiml,
    headers: {
      'Content-Type': 'text/xml'
    }
  }
};
